import React, { useState } from "react";

function Profile() {
	const [username, setUsername] = useState("ExampleUser");
	const [email, setEmail] = useState("thomas@gmail.com");
	const [password, setPassword] = useState("");
	const [isEditing, setIsEditing] = useState(false);

	const handleEditClick = () => {
		setIsEditing(true);
	};

	const handleSaveClick = () => {
		// Here you can add code to save the updated username, email, and password to your database.
		// For this example, we'll just update the state.
		setIsEditing(false);
	};

	const handleDeleteClick = async (e) => {
		e.preventDefault();

		try {
			const response = await fetch("http://localhost:4000/users/delete", {
				method: "POST",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify({ email }),
			});

			if (response.status === 200) {
				//successful delete
				const data = await response.json();
				console.log("Delete successful");
			} else if (response.status === 401) {
				// Invalid email or password
				const errorData = await response.json();
				console.log("Delete failed: " + errorData.error);
			} else {
				// Handle other error cases
				console.log("Server error");
			}
		} catch (error) {
			console.error("Error:", error);
		}
	};

	return (
		<div className="profile">
			<h2>Profile</h2>
			<div>
				<label>Username:</label>
				{isEditing ? (
					<input
						type="text"
						value={username}
						onChange={(e) => setUsername(e.target.value)}
					/>
				) : (
					<span>{username}</span>
				)}
			</div>
			<div>
				<label>Email:</label>
				{isEditing ? (
					<input
						type="email"
						value={email}
						onChange={(e) => setEmail(e.target.value)}
					/>
				) : (
					<span>{email}</span>
				)}
			</div>
			{isEditing && (
				<div>
					<label>Password:</label>
					<input
						type="password"
						value={password}
						onChange={(e) => setPassword(e.target.value)}
					/>
				</div>
			)}
			<div className="buttons">
				{isEditing ? (
					<button onClick={handleSaveClick}>Save</button>
				) : (
					<>
						<button onClick={handleEditClick}>Edit</button>
						<button onClick={handleDeleteClick}>
							Delete account
						</button>
					</>
				)}
			</div>
		</div>
	);
}

export default Profile;
