import React, { useState } from "react";
import { Link } from "react-router-dom";

function Profile({ user }) {
	const [currentUser, setUser] = useState(user);
	const [isEditing, setIsEditing] = useState(false);

	const handleEditClick = () => {
		setIsEditing(true);
	};

	const handleSaveClick = () => {
		setIsEditing(false);
		//get user id from local storage then update user info
	};

	const handleDeleteClick = async (e) => {
		e.preventDefault();

		try {
			const response = await fetch("http://localhost:4000/users/delete", {
				method: "POST",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify(currentUser.email),
			});

			if (response.status === 200) {
				//successful delete
				const data = await response.json();
				console.log("Delete successful");
				//go to login screen
			} else if (response.status === 401) {
				// Invalid email or password
				const errorData = await response.json();
				console.log("Delete failed: " + errorData.error);
			} else {
				// Handle other error cases
				console.log("Server error");
			}
		} catch (error) {
			console.error("Error:", error);
		}
	};

	return (
		<div className="profile">
			<h2>Profile</h2>
			<div>
				<label>Username:</label>
				{isEditing ? (
					<input
						type="text"
						value={currentUser.username}
						onChange={(e) =>
							setUser({
								...currentUser,
								username: e.target.value,
							})
						}
					/>
				) : (
					<span>{currentUser.username}</span>
				)}
			</div>
			<div>
				<label>Email:</label>
				{isEditing ? (
					<input
						type="email"
						value={currentUser.email}
						onChange={(e) =>
							setUser({ ...currentUser, email: e.target.value })
						}
					/>
				) : (
					<span>{currentUser.email}</span>
				)}
			</div>
			{/* {isEditing && (
				<div>
					<label>Password:</label>
					<input
						type="password"
						value={password}
						onChange={(e) => setPassword(e.target.value)}
					/>
				</div>
			)} */}
			<div className="buttons">
				{isEditing ? (
					<button onClick={handleSaveClick}>Save</button>
				) : (
					<>
						<button onClick={handleEditClick}>Edit</button>
						<button onClick={handleDeleteClick}>
							Delete account
						</button>
					</>
				)}
			</div>
			<Link to="/">Dashboard</Link>
		</div>
	);
}

export default Profile;
