import React, { Component } from "react";

class ForgotPassword extends Component {
	constructor() {
		super();
		this.state = {
			email: "",
			password: "",
			// createError: "", // Add a state for displaying create user errors
			updateSuccess: "", // Add a state for displaying create user success
		};
	}

	handleChange = (e) => {
		this.setState({
			[e.target.name]: e.target.value,
		});
	};

	handleSubmit = async (e) => {
		e.preventDefault();

		// You can add your registration logic here
		const { email, password } = this.state;

		try {
			const response = await fetch(
				"http://localhost:4000/update/password",
				{
					method: "POST",
					headers: {
						"Content-Type": "application/json",
					},
					body: JSON.stringify({ email, password }),
				}
			);

			if (response.status === 200) {
				// Successful registration
				const data = await response.json();
				this.setState({ updateSuccess: data.message });
				console.log("Updated password successfully");
			} else {
				// Handle other error cases
				// this.setState({ createError: data.message, createSuccess: "" });
				console.log("Server error");
			}
		} catch (error) {
			console.error("Error:", error);
		}
	};

	render() {
		const { email, password, updateSuccess } = this.state;

		return (
			<div className="register-page">
				<h1>Forgot Password</h1>
				<form onSubmit={this.handleSubmit}>
					<div className="form-group">
						<label htmlFor="email">Email:</label>
						<input
							type="email"
							id="email"
							name="email"
							value={email}
							onChange={this.handleChange}
							required
						/>
					</div>
					<div className="form-group">
						<label htmlFor="password">Password:</label>
						<input
							type="password"
							id="password"
							name="password"
							value={password}
							onChange={this.handleChange}
							required
						/>
					</div>
					{updateSuccess && (
						<p className="success">{updateSuccess}</p>
					)}
					<button type="submit">Reset Password</button>
				</form>
				<div>
					<a href="/">Back to Login</a>
				</div>
			</div>
		);
	}
}

export default ForgotPassword;
