import React, { Component } from "react";
// import { Link } from "react-router-dom";
// import { useNavigate } from "react-router-dom";
import { Navigate } from "react-router-dom";

class LoginPage extends Component {
	constructor() {
		super();
		this.state = {
			email: "",
			password: "",
			loginError: "", // Add a state for displaying login errors
			loginSuccess: "", // Add a state for displaying login success
		};
	}

	handleChange = (e) => {
		this.setState({
			[e.target.name]: e.target.value,
		});
	};

	handleSubmit = async (e) => {
		e.preventDefault();

		const { email, password } = this.state;

		// Make an HTTP POST request to the server's /users/login endpoint
		try {
			const response = await fetch("http://localhost:4000/users/login", {
				method: "POST",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify({ email, password }),
			});

			if (response.status === 200) {
				// Successful login
				const data = await response.json();
				this.setState({ loginSuccess: data.message, loginError: "" });
				console.log("Login successful");
				<Navigate to="/home" />;
			} else if (response.status === 401) {
				// Invalid email or password
				const errorData = await response.json();
				this.setState({
					loginError: errorData.error,
					loginSuccess: "",
				});
				console.log("Login failed: " + errorData.error);
			} else {
				// Handle other error cases
				console.log("Server error");
			}
		} catch (error) {
			console.error("Error:", error);
		}
	};

	render() {
		const { email, password, loginError, loginSuccess } = this.state;

		return (
			<div className="login-page">
				<h1>Login</h1>
				<form onSubmit={this.handleSubmit}>
					<div className="form-group">
						<label htmlFor="email">Email:</label>
						<input
							type="email"
							id="email"
							name="email"
							value={email}
							onChange={this.handleChange}
							required
						/>
					</div>
					<div className="form-group">
						<label htmlFor="password">Password:</label>
						<input
							type="password"
							id="password"
							name="password"
							value={password}
							onChange={this.handleChange}
							required
						/>
					</div>
					{loginError && <p className="error">{loginError}</p>}
					{loginSuccess && <p className="success">{loginSuccess}</p>}
					<button type="submit">Login</button>
				</form>
				<p>
					{/* Not a user? <Link to="/register">Sign Up now</Link> */}
				</p>
				<div>
					<a href="/register">Not a user? Sign up now</a>
				</div>
				<div>
					<a href="/forgot-password">Forgot password? Reset here</a>
				</div>
			</div>
		);
	}
}

export default LoginPage;
