import React, { useState } from "react";
import { Link } from "react-router-dom";

function Profile({ user, handleUserChange }) {
	const [isEditing, setIsEditing] = useState(false);
	const [localUser, setLocalUser] = useState({
		username: user.username,
		email: user.email,
	});

	const handleEditClick = () => {
		setIsEditing(true);
	};

	const handleSaveClick = async (e) => {
		e.preventDefault();
		setIsEditing(false);
		handleUserChange(localUser);
		//get user id from local storage then update user info
		try {
			console.log(user);
			const response = await fetch(
				`http://localhost:4000/users/update/${user.user_id}`,
				{
					method: "POST",
					headers: {
						"Content-Type": "application/json",
					},
					body: JSON.stringify(localUser),
				}
			);

			if (response.status === 200) {
				//successful update
				const data = await response.json();
				console.log("Update successful");
			} else {
				// Handle other error cases
				console.log("Server error");
			}
		} catch (error) {
			console.error("Error:", error);
		}
	};

	const handleDeleteClick = async (e) => {
		e.preventDefault();
		// Show a confirmation dialog
		const confirmed = window.confirm(
			"Are you sure you want to delete this user?"
		);

		if (confirmed) {
			try {
				const response = await fetch(
					"http://localhost:4000/users/delete",
					{
						method: "POST",
						headers: {
							"Content-Type": "application/json",
						},
						body: JSON.stringify(user.email),
					}
				);

				if (response.status === 200) {
					//successful delete
					const data = await response.json();
					console.log("Delete successful");
					//go to login screen
				} else if (response.status === 401) {
					// Invalid email or password
					const errorData = await response.json();
					console.log("Delete failed: " + errorData.error);
				} else {
					// Handle other error cases
					console.log("Server error");
				}
			} catch (error) {
				console.error("Error:", error);
			}
		}
	};

	return (
		<div className="profile">
			<h2>Profile</h2>
			<div>
				<label>Username:</label>
				{isEditing ? (
					<input
						type="text"
						onChange={(e) => {
							setLocalUser({
								...localUser,
								username: e.target.value,
							});
						}}
						value={localUser.username}
						name="username"
					/>
				) : (
					<span>{user.username}</span>
				)}
			</div>
			<div>
				<label>Email:</label>
				{isEditing ? (
					<input
						type="email"
						onChange={(e) => {
							setLocalUser({
								...localUser,
								email: e.target.value,
							});
						}}
						value={localUser.email}
						name="email"
					/>
				) : (
					<span>{user.email}</span>
				)}
			</div>
			<div className="buttons">
				{isEditing ? (
					<button onClick={(e) => handleSaveClick(e)}>Save</button>
				) : (
					<>
						<button onClick={handleEditClick}>Edit</button>
						<button onClick={handleDeleteClick}>
							Delete account
						</button>
					</>
				)}
			</div>
			<Link to="/">Dashboard</Link>
		</div>
	);
}

export default Profile;
