import React, { useState } from "react";
import { Link } from "react-router-dom";

function Profile({ user, handleUserChange }) {
	const [isEditing, setIsEditing] = useState(false);

	const handleEditClick = () => {
		setIsEditing(true);
	};

	const handleSaveClick = (e) => {
		e.preventDefault();
		setIsEditing(false);
		handleUserChange(e.target.value.username, e.target.value.email);
		//get user id from local storage then update user info
	};

	const handleDeleteClick = async (e) => {
		e.preventDefault();

		try {
			const response = await fetch("http://localhost:4000/users/delete", {
				method: "POST",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify(user.email),
			});

			if (response.status === 200) {
				//successful delete
				const data = await response.json();
				console.log("Delete successful");
				//go to login screen
			} else if (response.status === 401) {
				// Invalid email or password
				const errorData = await response.json();
				console.log("Delete failed: " + errorData.error);
			} else {
				// Handle other error cases
				console.log("Server error");
			}
		} catch (error) {
			console.error("Error:", error);
		}
	};

	return (
		<div className="profile">
			<h2>Profile</h2>
			<div>
				<label>Username:</label>
				{isEditing ? (
					<input type="text" value={user.username} name="username" />
				) : (
					<span>{user.username}</span>
				)}
			</div>
			<div>
				<label>Email:</label>
				{isEditing ? (
					<input type="email" value={user.email} name="email" />
				) : (
					<span>{user.email}</span>
				)}
			</div>
			{/* {isEditing && (
				<div>
					<label>Password:</label>
					<input
						type="password"
						value={password}
						onChange={(e) => setPassword(e.target.value)}
					/>
				</div>
			)} */}
			<div className="buttons">
				{isEditing ? (
					<button onClick={(e) => handleSaveClick(e)}>Save</button>
				) : (
					<>
						<button onClick={handleEditClick}>Edit</button>
						<button onClick={handleDeleteClick}>
							Delete account
						</button>
					</>
				)}
			</div>
			<Link to="/">Dashboard</Link>
		</div>
	);
}

export default Profile;
