import React, { useState } from "react";

function ForgotPassword() {
	const [email, setEmail] = useState("");
	const [newPassword, setNewPassword] = useState(""); // New password state
	const [message, setMessage] = useState("");

	const handleEmailChange = (e) => {
		setEmail(e.target.value);
	};

	const handleNewPasswordChange = (e) => {
		setNewPassword(e.target.value);
	};

	const handleSubmit = (e) => {
		e.preventDefault();

		// Here, you would typically send a request to your server to handle the password reset
		// For this example, we'll simulate it by displaying a message.

		// Replace this with your actual API endpoint
		const resetPasswordEndpoint = "/api/reset-password";

		fetch(resetPasswordEndpoint, {
			method: "POST",
			headers: {
				"Content-Type": "application/json",
			},
			body: JSON.stringify({ email, newPassword }), // Include the new password
		})
			.then((response) => response.json())
			.then((data) => {
				setMessage(data.message);
			})
			.catch((error) => {
				setMessage("An error occurred. Please try again later.");
			});
	};

	return (
		<div>
			<h2>Forgot Password</h2>
			<form onSubmit={handleSubmit}>
				<div>
					<label htmlFor="email">Email:</label>
					<input
						type="email"
						id="email"
						name="email"
						value={email}
						onChange={handleEmailChange}
						required
					/>
				</div>
				<div>
					<label htmlFor="newPassword">New Password:</label>{" "}
					{/* New Password input */}
					<input
						type="password"
						id="newPassword"
						name="newPassword"
						value={newPassword}
						onChange={handleNewPasswordChange}
						required
					/>
				</div>
				<div>
					<button type="submit">Reset Password</button>
				</div>
			</form>
			<p>{message}</p>
			<div>
				<a href="/">Back to Login</a>
			</div>
		</div>
	);
}

export default ForgotPassword;
