import React, { Component } from "react";

class Register extends Component {
	constructor() {
		super();
		this.state = {
			username: "",
			email: "",
			password: "",
			// createError: "", // Add a state for displaying create user errors
			createSuccess: "", // Add a state for displaying create user success
		};
	}

	handleChange = (e) => {
		this.setState({
			[e.target.name]: e.target.value,
		});
	};

	handleSubmit = async (e) => {
		e.preventDefault();

		// You can add your registration logic here
		const { username, email, password } = this.state;

		// Example: Send a registration request to the server
		// You can replace this with your actual API call
		try {
			const response = await fetch(
				"http://localhost:4000/users/register",
				{
					method: "POST",
					headers: {
						"Content-Type": "application/json",
					},
					body: JSON.stringify({ username, email, password }),
				}
			);

			if (response.status === 200) {
				// Successful registration
				const data = await response.json();
				this.setState({ createSuccess: data.message, createError: "" });
				console.log("Create user successful");
			} else {
				// Handle other error cases
				// this.setState({ createError: data.message, createSuccess: "" });
				console.log("Server error");
			}
		} catch (error) {
			console.error("Error:", error);
		}
	};

	render() {
		const { username, email, password, createSuccess } = this.state;

		return (
			<div className="register-page">
				<h1>Create Account</h1>
				<form onSubmit={this.handleSubmit}>
					<div className="form-group">
						<label htmlFor="username">Username:</label>
						<input
							type="text"
							id="username"
							name="username"
							value={username}
							onChange={this.handleChange}
							required
						/>
					</div>
					<div className="form-group">
						<label htmlFor="email">Email:</label>
						<input
							type="email"
							id="email"
							name="email"
							value={email}
							onChange={this.handleChange}
							required
						/>
					</div>
					<div className="form-group">
						<label htmlFor="password">Password:</label>
						<input
							type="password"
							id="password"
							name="password"
							value={password}
							onChange={this.handleChange}
							required
						/>
					</div>
					{createSuccess && (
						<p className="success">{createSuccess}</p>
					)}
					<button type="submit">Create Account</button>
				</form>
				<p>
					{/* Already a user? <Link to="/register">Login now</Link> */}
				</p>
				<div>
					<a href="/login">Already a user? Login now</a>
				</div>
			</div>
		);
	}
}

export default Register;
